    id INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(60),
    codigoBarras VARCHAR(12),
    preco1 DECIMAL(10, 2),
    preco2 DECIMAL(10, 2),
    descricao TEXT,
    categoria VARCHAR(100),
    isdeleted CHAR(1)
);

